import React from "react";

import Titles from "./Titles";
import Description from "./Description";
import Tabs from "./Tabs";
const API = '/book/';

class BookInfo extends React.Component {
  state = {
    isbn: this.props.match.params.isbn,
    response: '',
    responseToPost: '',
  };

  componentWillMount(){
    this.callApi()
    .then(res => this.setState({ response: res.express }))
    .catch(err => console.log(err));
    }

    callApi = async () => {
      const response = await fetch(API + this.state.isbn, {method: 'POST'});
      const body = await response.json();
      if (response.status !== 200) throw Error(body.message);
      return body;
    };

  render() {
    return (
      <div>
	<Titles />
	<Description  title = {this.state.isbn} description = {this.state.response} price = "9.00" cover = "https://images.penguinrandomhouse.com/cover/9780143128571" author = "William Shakespeare"/>
	<Tabs />
      </div>
    );
  }
};

export default BookInfo;
