import React from "react";

import Titles from "./Titles";
import Description from "./Description";
import Tabs from "./Tabs";

class BookInfo extends React.Component {

  render() {
    return (
      <div>
	<Titles />
	<Description  title = {props.location.state.title} description = "Romeo and Juliet is about two young star-crossed lovers whose deaths ultimately reconcile their feuding families. It was among Shakespeare's most popular plays during his lifetime and along with Hamlet, is one of his most frequently performed plays. " price = "9.00" cover = "https://images.penguinrandomhouse.com/cover/9780143128571" author = "William Shakespeare"/>
	<Tabs />
      </div>
    );
  }
};

export default BookInfo;
