import React from "react";

import Titles from "./Titles";
import Description from "./Description";
import Tabs from "./Tabs";
const API = '/book/';

class BookInfo extends React.Component {
  state = {
    isbn: this.props.match.params.isbn,
    // response: {title: 'defa',
    //             authorFirst: '',
    //             authorLast: '',
    //             cover: '',
    //             genre: '',
    //             publisher: '',
    //             avgRating: '',
    //             description: '',
    //             biography: ''},
    response: [],
    responseToPost: '',
  };

  componentWillMount(){
    var apiCall = fetch(API + this.state.isbn, {method: 'POST'});

    apiCall.then(response => {
      return response.json();
      }).then(result => {
         this.setState({
          response: result
        })
       });
    };

  render() {
    return (
      <div>
      {this.state.response.map(d => 
        {const name = (d.title);
        const des = (d.description);
        const firstName = (d.authorFirst);
        const lastName = (d.authorLast);
        const image = (d.cover);
        const prc = (d.price)})}
	<Titles />
	<Description  title = {name} description = {des}
      price = {prc} cover = "https://images.penguinrandomhouse.com/cover/9780143128571" author = "William Shakespeare"/>
	<Tabs />
      </div>
    );
  }
};

export default BookInfo;
