  };

  render() {
    return (
      <div>
	<Titles />
	<Description  title = {this.state.isbn} description = {this.state.responseToPost} price = "9.00" cover = "https://images.penguinrandomhouse.com/cover/9780143128571" author = "William Shakespeare"/>
	<Tabs />
      </div>
    );
  }
};

export default BookInfo;
