import React from "react";

import Titles from "./Titles";
import Description from "./Description";
import Tabs from "./Tabs";
const API = '/book/';

class BookInfo extends React.Component {
  state = {
    isbn: this.props.match.params.isbn,
    // response: {title: 'defa',
    //             authorFirst: '',
    //             authorLast: '',
    //             cover: '',
    //             genre: '',
    //             publisher: '',
    //             avgRating: '',
    //             description: '',
    //             biography: ''},
    response: [],
    responseToPost: '',
  };

  componentWillMount(){
    var apiCall = fetch(API + this.state.isbn, {method: 'POST'});

    apiCall.then(response => {
      return response.json();
      }).then(result => {
         this.setState({
          response: result
        })
       });
    };

  render() {
    const renderItems = this.state.response.map(function(response, i) {
      return <li key={i}>{response.title}</li>
    });
    return (
      <div>
	<Titles />
	<Description  title = {this.state.isbn} description = {this.state.response[0]} price = "9.00" cover = "https://images.penguinrandomhouse.com/cover/9780143128571" author = "William Shakespeare"/>
	<Tabs />
      </div>
    );
  }
};

export default BookInfo;
